import React from 'react';
import AliceCarousel from 'react-alice-carousel';
import 'react-alice-carousel/lib/alice-carousel.css';

export default function Carousel() {
  const responsive = {
    0: { 
        items: 1
    },
    568: { 
        items: 2
    },
    1024: {
        items: 3, 
        itemsFit: 'contain'
    },
};

const thumbItems = (items, [setThumbIndex, setThumbAnimation]) => {
    return items.map((item, i) => (
        <div className="thumb" onClick={() => (setThumbIndex(i), setThumbAnimation(true))}>
            {item}
        </div>
    ));
};

const items = [
    <div className="item" data-value="1"><img src="https://picsum.photos/400/300"/></div>,
    <div className="item" data-value="2"><img src="https://picsum.photos/400/300"/></div>,
    <div className="item" data-value="3"><img src="https://picsum.photos/400/300"/></div>,
    <div className="item" data-value="4"><img src="https://picsum.photos/400/300"/></div>,
    <div className="item" data-value="5"><img src="https://picsum.photos/400/300"/></div>,
    <div className="item" data-value="6"><img src="https://picsum.photos/400/300"/></div>
];
  return (  
      <div>
        <AliceCarousel
            mouseTracking
            items={items}
            responsive={responsive}
        />
      </div>
  )
}
