import React,{Component} from 'react';
import OwlCarousel from 'react-owl-carousel';
import 'owl.carousel/dist/assets/owl.carousel.css'; 
import 'owl.carousel/dist/assets/owl.theme.default.css'; 

import { useEffect, useState } from "react";
import axios from "axios";

export default function Ofertas() {
    const [users, setUsers] = useState([]);

    useEffect(() => {
        axios
        .get("./produtos.json")
        .then((res) => setUsers(res.data))
        .catch((err) => console.log(err));
    }, []);  

    return (
      <div className="slider-produtos">  
      {users.map((user, index) => (
          <li key={index}>
            #{user.id}: {user.image} {user.titulo} {user.subtitulo}
          </li>
        ))}
            <OwlCarousel nav={true} items={3} margin={8} autoplay={false} navSpeed={true}> 

                <div className="internal-item">
                    <img className="prod-img" src= {'images/shampoo.jpg'}/>
                    <div class="title">
                        <span>Kit NIELY Gold Pós Quimica Óleo de Argan</span>
                        <span>Shampoo 300ml + Condicionador 200ml</span>
                    </div>
                    <div class="description">
                        <div class="desc-left">
                                <p>2x <span>R$9,40</span><br/>
                                à vista <span>R$18,79</span></p>
                        </div>
                        <div class="desc-right">
                                <img src="images/cards.png" alt="Cartões Master e Visa"/>
                        </div>                                        
                    </div>    
                </div> 

                <div className="internal-item"><img className="prod-img" src= {'images/papel.jpg'}/></div>  

                <div className="internal-item"><img  className="prod-img" src= {'images/shampoo-phyto.jpg'}/></div>  

                <div className="internal-item"><img  className="prod-img" src= {'images/sabonete.jpg'}/></div>  

                <div className="internal-item"><img className="prod-img" src= {'images/sabonete2.jpg'}/></div>  

                <div className="internal-item"><img className="prod-img" src= {'images/shampoo3.jpg'}/></div>
            </OwlCarousel>  
        </div>  

      
    );
  }
